/* The eearth  Inputs: latitude, longitude, year, minute of the year, orientation (compass heading, 0->2pi), inclination (0=vertical?)Outputs: a coordinate system, position of the sun, position of the moon,  */#import <Foundation/Foundation.h>#import "mVec3.h"#import "mx4.h"@interface eearth : NSObject {    double lat, lon;     double     double dayOfYear, yearFract;    double inclination, heading;    int clockCounter;         // given allllll that other shit, here's you    mVec3 p, u, d;         // given that you're there, some directions for sanity-checking    mVec3 dueNorth, dueEast;         // hey! and here's the sun. I want the moon, too.     mVec3 sunPos;        // helpful shit    double dayDt, yearDt; }- (void)takeLatLon:(float)latin:(float)longing;- (void)takeHeading:(float)hin; - (void)takeIncline:(float)i;// seconds since Jan 1, divided by sec/day - (double)getDayOfYear;- (void)getPUD:(mVec3*)p:(mVec3*)u:(mVec3*)d;- (void)getNE:(mVec3*)n:(mVec3*)e; - (void)getSun:(mVec3*)s; // call giving seconds per frame- (void)update:(double)dt; @end